mapper.update:
UPDATE ${uid} SET
word = #{word},
meaning = #{meaning}
WHERE card_num = #{card_num}

mapper.select:

SELECT * 
FROM ${uid}
WHERE list_name=#{list_name}
AND important = 'y'
LIMIT #{impStartList}, #{impListSize}

SELECT
	count(*) as listCnt
FROM
	${uid}
WHERE list_name=#{list_name}

SELECT
	count(*) as listCnt
FROM
	${uid}
WHERE list_name=#{list_name} 
AND important='y'
